import React, { useState } from 'react';

const BookForm = ({ onSubmit, initialBook = null }) => {
  const [title, setTitle] = useState(initialBook?.title || '');
  const [author, setAuthor] = useState(initialBook?.author || '');
  const [description, setDescription] = useState(initialBook?.description || '');
  const [coverImage, setCoverImage] = useState(initialBook?.coverImage || '');
  const [audioFile, setAudioFile] = useState(initialBook?.audioFile || '');
  const [category, setCategory] = useState(initialBook?.category || '');
  const [duration, setDuration] = useState(initialBook?.duration || 0);
  const [genre, setGenre] = useState(initialBook?.genre || []);
  const [rating, setRating] = useState(initialBook?.rating || 0);
  const [chapters, setChapters] = useState(initialBook?.chapters || []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const bookData = {
      title,
      author,
      description,
      coverImage,
      audioFile,
      category,
      duration,
      genre,
      rating,
      chapters
    };

    try {
      const response = await fetch('http://localhost:9000/api/books', {
        method: initialBook ? 'PUT' : 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify(bookData)
      });

      if (response.ok) {
        onSubmit();
      } else {
        const errorData = await response.json();
        console.error('Error submitting book:', errorData);
        alert(`Error: ${errorData.message || 'Failed to create/update book'}`);
      }
    } catch (error) {
      console.error('Network error:', error);
      alert('Network error. Please try again.');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      {/* Existing inputs */}
      <input
        type="number"
        placeholder="Duration (seconds)"
        value={duration}
        onChange={(e) => setDuration(Number(e.target.value))}
        className="w-full p-2 border rounded"
      />
      <input
        type="text"
        placeholder="Genres (comma-separated)"
        value={genre.join(', ')}
        onChange={(e) => setGenre(e.target.value.split(',').map(g => g.trim()))}
        className="w-full p-2 border rounded"
      />
      <input
        type="number"
        step="0.1"
        placeholder="Rating"
        value={rating}
        onChange={(e) => setRating(Number(e.target.value))}
        className="w-full p-2 border rounded"
      />
      <button 
        type="submit" 
        className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
      >
        {initialBook ? 'Update Book' : 'Add Book'}
      </button>
    </form>
  );
};

export default BookForm;
