import React, { useEffect } from 'react';
import AudioPlayer from 'react-h5-audio-player';
import 'react-h5-audio-player/lib/styles.css';

const AudioBookPlayer = ({ audioUrl, onProgress }) => {
  useEffect(() => {
    console.log('AudioPlayer received URL:', audioUrl);
  }, [audioUrl]);

  if (!audioUrl) {
    return <div className="text-red-500">No audio URL provided</div>;
  }

  return (
    <div className="w-full max-w-3xl mx-auto bg-white rounded-lg shadow-md p-4">
      <AudioPlayer
        autoPlay={false}
        src={audioUrl}
        showJumpControls={true}
        showFilledVolume={true}
        layout="horizontal"
        customControlsSection={[
          'MAIN_CONTROLS',
          'VOLUME_CONTROLS',
          'ADDITIONAL_CONTROLS',
        ]}
        onListen={(e) => onProgress(e.target.currentTime)}
        onError={(e) => {
          console.error('Audio player error:', e);
          console.log('Failed to load URL:', audioUrl);
        }}
        style={{
          width: '100%',
          borderRadius: '8px',
        }}
        customStyles={{
          progressBar: {
            backgroundColor: '#e0e0e0',
          },
          progress: {
            backgroundColor: '#4CAF50',
          },
        }}
      />
    </div>
  );
};

export default AudioBookPlayer;
