import React, { useState } from 'react';

const BookForm = ({ onSubmit, initialBook = null }) => {
  const [title, setTitle] = useState(initialBook?.title || '');
  const [author, setAuthor] = useState(initialBook?.author || '');
  const [description, setDescription] = useState(initialBook?.description || '');
  const [coverImage, setCoverImage] = useState(initialBook?.coverImage || '');
  const [audioFile, setAudioFile] = useState(initialBook?.audioFile || '');
  const [category, setCategory] = useState(initialBook?.category || '');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const bookData = {
      title,
      author,
      description,
      coverImage,
      audioFile,
      category
    };

    try {
      const response = await fetch('http://localhost:9000/api/books', {
        method: initialBook ? 'PUT' : 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify(bookData)
      });

      if (response.ok) {
        onSubmit();
      } else {
        const errorData = await response.json();
        console.error('Error submitting book:', errorData);
      }
    } catch (error) {
      console.error('Network error:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <input
        type="text"
        placeholder="Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        required
        className="w-full p-2 border rounded"
      />
      <input
        type="text"
        placeholder="Author"
        value={author}
        onChange={(e) => setAuthor(e.target.value)}
        required
        className="w-full p-2 border rounded"
      />
      <textarea
        placeholder="Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        className="w-full p-2 border rounded"
      />
      <input
        type="text"
        placeholder="Cover Image URL"
        value={coverImage}
        onChange={(e) => setCoverImage(e.target.value)}
        className="w-full p-2 border rounded"
      />
      <input
        type="text"
        placeholder="Audio File URL"
        value={audioFile}
        onChange={(e) => setAudioFile(e.target.value)}
        className="w-full p-2 border rounded"
      />
      <input
        type="text"
        placeholder="Category"
        value={category}
        onChange={(e) => setCategory(e.target.value)}
        className="w-full p-2 border rounded"
      />
      <button 
        type="submit" 
        className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
      >
        {initialBook ? 'Update Book' : 'Add Book'}
      </button>
    </form>
  );
};

export default BookForm;
